let ytubeplaybtn=document.querySelectorAll(".youtube-video-ts .play");ytubeplaybtn.forEach(e=>{e.addEventListener("click",e=>{let t=e.target.parentNode.closest("[data-yt-src]"),r=t.getAttribute("data-yt-src"),a=t.getAttribute("width"),l=t.getAttribute("height");t.innerHTML=`<iframe src="${r}" width="${a}" height="${l}" frameborder="0" allowfullscreen="1" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"></iframe>`})});